import com.codoid.products.exception.FilloException;
import com.codoid.products.fillo.Connection;
import com.codoid.products.fillo.Fillo;
import com.codoid.products.fillo.Recordset;



Fillo fillo=new Fillo();
Connection connection;
connection= fillo.getConnection("C:\\Users\\syednaseru\\Desktop\\TestData.xlsx");
String strQuery="Select * from Pets";
Recordset recordset=connection.executeQuery(strQuery);
while(recordset.next()) {
     ArrayList<String> ColCollection = recordset.getFieldNames();
     //log.info(ColCollection)
     int size = ColCollection.size();
     for (int Iter = 0; Iter <size ; Iter++) {
           String ColName = ColCollection.get(Iter);
          String ColValue = recordset.getField(ColName);
             //log.info(ColName) 
              //log.info(ColValue) 
        testRunner.testCase.setPropertyValue(ColName,ColValue)
      }
      //log.info(testRunner.testCase.testSuite.getTestCaseList().size())
	def testCaseList = testRunner.testCase.testSuite.getTestCaseList()
	for(int i = 0; i<testCaseList.size();i++){
		testStepList = testCaseList.get(i).getTestStepList()
		for(int j = 0;j<testStepList.size();j++){
			log.info(testStepList.get(j).getName())
			if(!testStepList.get(j).getName().matches("RunTests")){
				testStepList.get(j).run(testRunner, context)
			}
		}
	}
}
          recordset.close();
         connection.close();

